  position: relative;
  top: -45px;
  left: 1200px;

  input {
    width: 200px;
    border: 0;
    border-bottom: 1px solid #000;
    font-size: 0.8125rem;
    line-height: 1.3125rem;
    // opacity: 0;
  }

  img {
    width: 16px;
    height: 16px;
    position: relative;
    left: 184px;
    top: -20px;
    cursor: pointer;
  }
`;

export const Search: React.FC = () => {
  // const { searchValue, setSearchValue } = React.useContext(SearchContext);

  return (
    <SearchRoot>
      <div className="inputClass">
        <input
          // value={searchValue}
          // onChange={(e) => setSearchValue(e.target.value)}
          // className={onActiveInput}
          placeholder="Что Вы ищете в W магази?"
        />
      </div>

      <img src="/img/serach11.svg" alt="Search" />
    </SearchRoot>
  );
};
