import axios from "axios";
import { createSlice, createAsyncThunk, PayloadAction } from "@reduxjs/toolkit";
import { RootState } from "./store";
import { Filter } from "./filterSlice";

type Item = {
  id: string;
  title: string;
  price: number;
  imageUrl: string;
  rating: number;
  date: number;
};

export enum Status {
  LOADING = "loading",
  SUCCESS = "success",
  ERROR = "error",
}

export type SearchItemParams = {
  sortBy: Filter;
  order: string;
  category: string;
  search: string;
};

export const fetchItems = createAsyncThunk<Item[], SearchItemParams>(
  "item/fetchItemsStatus",
  async (params) => {
    const { sortBy, order, category, search } = params;
    const { data } = await axios.get<Item[]>(
      `https://63c83bede52516043f4e9cbf.mockapi.io/items?category=${category}&sortBy=${sortBy}&order=${order}${search}`
    );
    return data;
  }
);

interface ItemSliceState {
  items: Item[];
  status: Status;
}

const initialState: ItemSliceState = {
  items: [],
  status: Status.LOADING,
};

const itemSlice = createSlice({
  name: "item",
  initialState,
  reducers: {
    setItems(state, action: PayloadAction<Item[]>) {
      state.items = action.payload;
    },
  },

  extraReducers(builder) {
    builder.addCase(fetchItems.pending, (state, action) => {
      state.status = Status.LOADING;
      state.items = [];
    });
    builder.addCase(fetchItems.fulfilled, (state, action) => {
      state.items = action.payload;
      state.status = Status.SUCCESS;
    });
    builder.addCase(fetchItems.rejected, (state, action) => {
      state.status = Status.ERROR;
      state.items = [];
    });
  },
});

export const selectItemsData = (state: RootState) => state.item;

export const { setItems } = itemSlice.actions;

export default itemSlice.reducer;
