import React from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const FullProduct = () => {
  const [product, setProduct] = React.useState();
  const { id } = useParams();

  React.useEffect(() => {
    async function fetchProducts() {
      try {
        const { data } = axios.get(
          "https://63c83bede52516043f4e9cbf.mockapi.io/items/" + id
        );
        setProduct(data);
      } catch (error) {
        alert("Ошибка при получении вещей");
      }
    }
  }, []);

  return (
    <div>
      <h1>
        Lorem, ipsum dolor sit amet consectetur adipisicing elit. Iusto eveniet
        praesentium, reprehenderit saepe laborum blanditiis accusamus nam
        quaerat perferendis numquam sint natus quam aut ad. Natus rem odio
        soluta obcaecati?
      </h1>
    </div>
  );
};

export default FullProduct;
