import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { setSort } from "../redux/filterSlice";

export const list = [
  { name: "Начать с новинок", sortProperty: "date" },
  { name: "По рейтингу", sortProperty: "rating" },
  { name: "От дешёвых к дорогим", sortProperty: "-price" },
  { name: "От дорогих к дешёвым", sortProperty: "price" },
];

export function Filters() {
  // const items = useSelector((state) => state);

  const totalCount = items.reduce((sum, item) => sum + item.count, 0);

  const dispatch = useDispatch();
  const sort = useSelector((state) => state.filter.sort);
  const sortRef = React.useRef();

  const [open, setOpen] = React.useState(false);

  const onClickListItem = (obj) => {
    dispatch(setSort(obj));
    setOpen(false);
  };

  React.useEffect(() => {
    const handleClickOutside = (event) => {
      let path = event.composedPath().includes(sortRef.current);
      if (!path) setOpen(false);
    };

    document.body.addEventListener("click", handleClickOutside);

    return () => document.body.removeEventListener("click", handleClickOutside);
  }, []);

  return (
    <div ref={sortRef} className="filters">
      <div className="content__slider">
        <div className="color">
          <button>
            <span>Цвет</span>
            <img
              src="./img/arrow__down.svg"
              width={15}
              height={15}
              alt="Arrow Down"></img>
          </button>
        </div>
        <div className="filters__content__right">
          <p>{totalCount} вещей</p>
          <div className="strip"></div>
          <div className="sorting">
            <span onClick={() => setOpen(!open)}>{sort.name}</span>
            <img
              src="./img/arrow__down.svg"
              width={15}
              height={15}
              alt="Arrow Down"></img>
          </div>
        </div>
      </div>
      {open && (
        <div className="sorting_down">
          <ul>
            {list.map((obj, i) => (
              <li
                key={i}
                onClick={() => onClickListItem(obj)}
                className={
                  sort.sortProperty === obj.sortProperty ? "active1" : ""
                }>
                {obj.name}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
    // <div className="filters">
