import React from "react";
import qs from "qs";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";

import { setCategoryId, setFilters } from "../redux/filterSlice";
import { categories, Categories } from "../components/Categories";
import { Filters } from "../components/Filters";
import { ItemBlock } from "../components/ItemBlock";
import axios from "axios";
import { SearchContext } from "../App";

const Shitrs = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch;
  const isSearch = React.useRef(false);
  const isMounted = React.useRef(false);

  const { categoryId, sort } = useSelector((state) => state.filter);

  const { searchValue } = React.useContext(SearchContext);
  const [items, setItems] = React.useState([]);

  const onChangeCategory = (id) => {
    dispatch(setCategoryId(id));
  };

  const fetchItems = () => {
    const sortBy = sort.sortProperty.replace("-", "");
    const order = sort.sortProperty.includes("-") ? "asc" : "desc";
    const search = searchValue ? `&search=${searchValue}` : "";

    axios
      .get(
        `https://63c83bede52516043f4e9cbf.mockapi.io/items?category=${categoryId}&sortBy=${sortBy}&order=${order}${search}`
      )
      .then((res) => {
        setItems(res.data);
      });
  };

  React.useEffect(() => {}, [categoryId, sort.sortProperty, searchValue]);

  React.useEffect(() => {
    if (isMounted.current) {
      const queryString = qs.stringify({
        sortProperty: sort.sortProperty,
        categoryId,
      });

      navigate(`/?${queryString}`);
    }
    isMounted.current = true;
  }, [categoryId, sort.sortProperty, searchValue]);

  React.useEffect(() => {
    if (window.location.search) {
      const params = qs.parse(window.location.search.substring(1));

      const sort = categories.find(
        (obj) => obj.sortProperty === params.sortProperty
      );

      dispatch(
        setFilters({
          ...params,
          sort,
        })
      );

      isSearch.current = true;
    }
  }, []);

  React.useEffect(() => {
    if (!isSearch.current) {
      fetchItems();
    }
  }, [categoryId, sort.sortProperty, searchValue]);

  const products = items
    .filter((obj) => {
      if (obj.title.toLowerCase().includes(searchValue.toLowerCase())) {
        return true;
      }

      return false;
    })
    .map((obj) => <ItemBlock key={obj.id} {...obj} />);

  return (
    <div className="shirts">
      <Categories value={categoryId} onChangeCategory={onChangeCategory} />
      <Filters />
      <div className="main">
        <div className="name">
          <span>ДЖИНСЫ</span>
        </div>
        <div className="description">
          <p>
            Эти джинсы Muse, изготовленные исключительно в Италии или Японии,
            меняют представление о джинсах, <br /> придавая им утонченный
            современный вид.
          </p>
        </div>
      </div>
      <div className="content">{products}</div>
    </div>
  );
};

export default Shitrs;
