import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { RootState } from "./store";

enum SortPropertyEnum {
  DATE = "date",
  RATING = "rating",
  PRICE_DESC = "price",
  PRICE_ASC = "-price",
}

export type Filter = {
  name: string;
  sortProperty: SortPropertyEnum;
};

export interface FilterSliceState {
  searchValue: string;
  categoryId: number;
  sort: Filter;
}

const initialState: FilterSliceState = {
  searchValue: "",
  categoryId: 0,
  sort: { name: "Начать с новинок", sortProperty: SortPropertyEnum.DATE },
};

const filterSlice = createSlice({
  name: "filters",
  initialState,
  reducers: {
    setCategoryId(state, action: PayloadAction<number>) {
      state.categoryId = action.payload;
    },
    // setSearchValue(state, action) {
    //   state.searchValue = action.payload;
    // },
    setSort(state, action: PayloadAction<Filter>) {
      state.sort = action.payload;
    },
    setFilters(state, action: PayloadAction<FilterSliceState>) {
      state.sort = action.payload.sort;
      state.categoryId = Number(action.payload.categoryId);
    },
  },
});

export const selectFilter = (state: RootState) => state.filter;
export const selectSortFilter = (state: RootState) => state.filter.sort;

export const { setCategoryId, setSort, setFilters } = filterSlice.actions;

export default filterSlice.reducer;
