import React from "react";
import qs from "qs";
import { useSelector, useDispatch } from "react-redux";
import { Link, useNavigate } from "react-router-dom";

import { selectFilter, setCategoryId, setFilters } from "../redux/filterSlice";
import { Categories } from "../components/Categories";
import { Filters, list } from "../components/Filters";
import ItemBlock from "../components/ItemBlock";
// import { SearchContext } from "../App";
import { fetchItems, selectItemsData } from "../redux/itemSlice";
import MyLoader from "../components/ItemBlock/Skeleton";

const Shitrs: React.FC = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const isSearch = React.useRef(false);
  const isMounted = React.useRef(false);

  const { items, status } = useSelector(selectItemsData);

  const { categoryId, sort } = useSelector(selectFilter);

  // const { searchValue } = React.useContext(SearchContext);

  const onChangeCategory = (idx: number) => {
    dispatch(setCategoryId(idx));
  };

  React.useEffect(() => {
    if (isMounted.current) {
      const queryString = qs.stringify({
        sortProperty: sort.sortProperty,
        categoryId,
      });

      navigate(`?${queryString}`);
    }
    isMounted.current = true;
  }, [categoryId, sort.sortProperty, navigate]);

  React.useEffect(() => {
    if (window.location.search) {
      const params = qs.parse(window.location.search.substring(1));

      const sort = list.find((obj) => obj.sortProperty === params.sortProperty);

      dispatch(
        setFilters({
          ...params,
          sort,
        })
      );

      isSearch.current = true;
    }
  }, [dispatch]);

  React.useEffect(() => {
    const getItems = async () => {
      const sortBy = sort.sortProperty.replace("-", "");
      const order = sort.sortProperty.includes("-") ? "asc" : "desc";
      const category = categoryId;
      // const search = searchValue ? `&search=${searchValue}` : "";

      dispatch(
        //@ts-ignore
        fetchItems({
          sortBy,
          order,
          category,
          // search,
        })
      );
    };
    getItems();
  }, [categoryId, sort.sortProperty, dispatch]);

  const products = items.map((obj: any) => (
    <Link key={obj.id} to={`/product/${obj.id}`}>
      <ItemBlock {...obj} />
    </Link>
  ));
  const skeletons = [...new Array(6)].map((_, index) => (
    <MyLoader key={index} />
  ));

  // .filter((obj) => {
  //   if (obj.title.toLowerCase().includes(searchValue.toLowerCase())) {
  //     return true;
  //   }

  //   return false;
  // })

  return (
    <div className="shirts">
      <Categories value={categoryId} onChangeCategory={onChangeCategory} />
      <Filters />
      <div className="main">
        <div className="name">
          <span>ДЖИНСЫ</span>
        </div>
        <div className="description">
          <p>
            Эти джинсы Muse, изготовленные исключительно в Италии или Японии,
            меняют представление о джинсах, <br /> придавая им утонченный
            современный вид.
          </p>
        </div>
      </div>
      <div className="content">
        {status === "loading" ? skeletons : products}
      </div>
    </div>
  );
};

export default Shitrs;
